	<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>		<header class="entry-header">			<h2 class="entry-title">				<a href="<?php the_permalink(); ?>" title="<?php the_title(); ?>"><?php the_title(); ?></a>			</h2>		</header>		<div class="entry-content">			<?php the_content(); ?>			<?php wp_link_pages( array( 'before' => '<div class="page-links">' . __( 'Pages:', 'mdframework' ), 'after' => '</div>' ) ); ?>		</div><!-- .entry-content -->		<footer class="entry-meta">			<?php edit_post_link( __( 'Edit', 'mdframework' ), '<span class="edit-link">', '</span>' ); ?>		</footer><!-- .entry-meta -->	</article><!-- #post --><!-- Fine Content-page.php -->