<?php/** * The main template file. * * This theme is purely for the purpose of testing theme options in Options Framework plugin. * * @package WordPress * @subpackage Options Framework Theme * Testata HomePage */if (of_get_option('enabling_test_homepage')){		get_template_part('parti/test','options');	}else{ $selezione = of_get_option('select_sitewide_template', '' );		switch ($selezione) {    case 'forum':        get_template_part('parti/'.$selezione.'/head',$selezione);/*Usually <head></head><body>*/		get_template_part('parti/'.$selezione.'/top',$selezione);/*<header></header><hgroup></hgroup><nav></nav>*/		get_template_part('parti/'.$selezione.'/home',$selezione);/*All the goodies */		get_template_part('parti/'.$selezione.'/footer',$selezione);/*End of the page */        break;    case 'blog':        get_template_part('parti/'.$selezione.'/head',$selezione);		get_template_part('parti/'.$selezione.'/top',$selezione);		get_template_part('parti/'.$selezione.'/home',$selezione);		get_template_part('parti/'.$selezione.'/footer',$selezione);        break;    case 'hotel':        get_template_part('parti/'.$selezione.'/head',$selezione);		get_template_part('parti/'.$selezione.'/top',$selezione);		get_template_part('parti/'.$selezione.'/home',$selezione);		get_template_part('parti/'.$selezione.'/footer',$selezione);        break;	case 'ecommerce':        get_template_part('parti/'.$selezione.'/head',$selezione);		get_template_part('parti/'.$selezione.'/top',$selezione);		get_template_part('parti/'.$selezione.'/home',$selezione);		get_template_part('parti/'.$selezione.'/footer',$selezione);        break;	case 'base':        get_template_part('parti/'.$selezione.'/head',$selezione);		get_template_part('parti/'.$selezione.'/top',$selezione);		get_template_part('parti/'.$selezione.'/home',$selezione);		get_template_part('parti/'.$selezione.'/footer',$selezione);        break;	default:		get_header();}; };?>